def york_contains(x: float, y: float) -> bool:
	return abs((x-3)-(y-17.5)/2.5) + abs((x-3)+(y-17.5)/2.5) <= 2

def alabai_contains(x: float, y: float) -> bool:
	return (x-63)**2 + 15/26 * (y-71)**2 <= 588


